import "dotenv/config";
import express from "express";
import cors from "cors";
import mongoose from "mongoose";

import router from "./routes/index"; // <- default export from routes/index.ts

const app = express();
const PORT = process.env.PORT || "4000";

/* â”€ Middle-wares â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
app.use(cors({ origin: true, credentials: true }));
app.use(express.json());

/* â”€ Routes â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
app.use("/api", router); // /api/health etc.

/* â”€ MongoDB connection â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
if (!process.env.MONGO_URI) {
  throw new Error("âŒ  MONGO_URI missing in .env file");
}

mongoose
  .connect(process.env.MONGO_URI)
  .then(() => {
    console.log("âœ…  MongoDB connected");
  })
  .catch((err) => {
    console.error("âŒ  Mongo connection error:", err);
    process.exit(1);
  });

/* â”€ Start the server â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
app.listen(PORT, () => {
  console.log(`ðŸš€  API running on http://localhost:${PORT}/api/health`);
});
